import React, { useEffect, useState } from 'react'
import CodeMirror from 'Codemirror'

import  Header  from "../shared/Header.js";
import  UserClients  from "../shared/sharedUserclients/UserClients";
import '../../assets/css/editor.css'



export default function Editor() {

    const [Users, setUsers] = useState([
        {socketId: 1, username: "Nafis Ayaz"},
        {socketId: 2, username: "Aleeza Ayaz"},
        {socketId: 2, username: "Kaushar Nisha"},
        {socketId: 1, username: "Farha Nasreen"},
    ])

    useEffect(()=>{
            async function init(){
                CodeMirror.fromTextArea(document.getElementById('E-editor') , {
                    mode: {
                        name: "Cpp"

                    }
                })
            }
            init()
    }, [])

    return (

            <React.Fragment>
                <Header/>
                <div className='mainWrapper'>
                    <div className='aside'>
                        <div className='asideInner'>
                            <h4>Connected</h4>
                            <div className='userLists'>
                                {Users.map((user) => (
                                <UserClients
                                    key={user.socketId}
                                    username = {user.username}
                                />
                                )) }
                            </div>
                        </div>
                        <button className='btn E-btncopy'>COPY ROOM ID</button>
                        <button className='btn E-btnleave'>LEAVE</button>
                    </div>
                    <div className='editorWrapper'>
                        <textarea id='E-editor'></textarea>
                    </div>
                </div>
          </React.Fragment>


    )
}
